// Replace this with your deployed backend URL or localhost
const API = axios.create({
  baseURL: "http://localhost:5000/api" // Example: http://localhost:5000/api
});

// Optional: Add interceptors for auth / headers if needed
// API.interceptors.request.use(config => {
//   config.headers["Authorization"] = `Bearer ${token}`;
//   return config;
// });

export default API;
