import React from "react";
import "./index.css";


function HeroSection({ hero }) {
  if (!hero) return null;

  return (
    <section
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        padding: "50px 20px",
        background: "#f5f5f5"
      }}
    >
      <div>
        <h1 style={{ fontSize: "2.5rem", marginBottom: "10px" }}>{hero.name}</h1>
        <h2 style={{ fontSize: "1.5rem", marginBottom: "10px" }}>{hero.title}</h2>
        <p style={{ fontSize: "1.2rem", color: "#555" }}>{hero.tagline}</p>
      </div>
      {hero.profileImage && (
        <div>
          <img
            src={hero.profileImage}
            alt={hero.name}
            style={{
              width: "200px",
              height: "200px",
              objectFit: "cover",
              borderRadius: "50%",
              border: "3px solid #ddd"
            }}
          />
        </div>
      )}
    </section>
  );
}

export default HeroSection;
