import React, { useState } from "react";

import API from "../api/portfolioApi";

function MultiSectionForm({ templateId, onSubmit }) {
  const [formData, setFormData] = useState({
    template: templateId || "",
    hero: { name: "", title: "", tagline: "", profileImage: "" },
    about: { bio: "", email: "", phone: "", location: "", socials: { linkedin: "", twitter: "" } },
    skills: [],
    services: [
      { title: "", description: "" },
      { title: "", description: "" },
      { title: "", description: "" }
    ],
    projects: [
      { title: "", image: "", description: "" },
      { title: "", image: "", description: "" },
      { title: "", image: "", description: "" }
    ],
    testimonials: [
      { client: "", quote: "" },
      { client: "", quote: "" },
      { client: "", quote: "" }
    ],
    blog: [{ title: "", summary: "" }],
    contact: { email: "", phone: "", message: "" }
  });

  const handleChange = (section, field, value, index = null, subfield = null) => {
    setFormData(prev => {
      const updated = { ...prev };
      if (index !== null) {
        if (subfield) {
          updated[section][index][subfield] = value;
        } else {
          updated[section][index][field] = value;
        }
      } else {
        if (subfield) {
          updated[section][field][subfield] = value;
        } else {
          updated[section][field] = value;
        }
      }
      return updated;
    });
  };

  const handleSkillChange = (e, index) => {
    const skillsCopy = [...formData.skills];
    skillsCopy[index] = e.target.value;
    setFormData(prev => ({ ...prev, skills: skillsCopy }));
  };

  const addSkill = () => {
    setFormData(prev => ({ ...prev, skills: [...prev.skills, ""] }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // API call to save portfolio
      const res = await API.post("/portfolios", { ...formData, template: templateId });
      alert("Portfolio created! ID: " + res.data._id);

      // Call the onSubmit callback to redirect
      if (onSubmit) {
        onSubmit(res.data._id);
      }
    } catch (err) {
      console.error(err);
      alert("Error creating portfolio");
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ padding: "20px" }}>
      {/* Hero Section */}
      <h2>Hero Section</h2>
      <input type="text" placeholder="Name" value={formData.hero.name} onChange={e => handleChange("hero","name", e.target.value)} /><br/>
      <input type="text" placeholder="Title" value={formData.hero.title} onChange={e => handleChange("hero","title", e.target.value)} /><br/>
      <input type="text" placeholder="Tagline" value={formData.hero.tagline} onChange={e => handleChange("hero","tagline", e.target.value)} /><br/>
      <input type="text" placeholder="Profile Image URL" value={formData.hero.profileImage} onChange={e => handleChange("hero","profileImage", e.target.value)} /><br/>

      {/* About Me Section */}
      <h2>About Me</h2>
      <textarea placeholder="Bio" value={formData.about.bio} onChange={e => handleChange("about","bio", e.target.value)} /><br/>
      <input type="email" placeholder="Email" value={formData.about.email} onChange={e => handleChange("about","email", e.target.value)} /><br/>
      <input type="text" placeholder="Phone" value={formData.about.phone} onChange={e => handleChange("about","phone", e.target.value)} /><br/>
      <input type="text" placeholder="Location" value={formData.about.location} onChange={e => handleChange("about","location", e.target.value)} /><br/>
      <input type="text" placeholder="LinkedIn" value={formData.about.socials.linkedin} onChange={e => handleChange("about","socials", e.target.value, null,"linkedin")} /><br/>
      <input type="text" placeholder="Twitter" value={formData.about.socials.twitter} onChange={e => handleChange("about","socials", e.target.value, null,"twitter")} /><br/>

      {/* Skills Section */}
      <h2>Skills</h2>
      {formData.skills.map((skill, i) => (
        <input key={i} type="text" placeholder={`Skill ${i+1}`} value={skill} onChange={e => handleSkillChange(e, i)} />
      ))}
      <button type="button" onClick={addSkill}>Add Skill</button>

      {/* Services, Projects, Testimonials, Blog, Contact sections... */}
      {/* (Yaha pe same previous code use hoga, jaise maine pehle diya tha) */}

      <button type="submit" style={{ marginTop: "20px" }}>Submit Portfolio</button>
    </form>
  );
}

export default MultiSectionForm;
