// src/api/portfolioApi.js
import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:3001', // Your json-server URL
});

// POST to create a new portfolio
export const createPortfolio = (portfolioData) => {
  return api.post('/portfolios', portfolioData);
};

// GET all portfolios (for the list page)
export const getAllPortfolios = () => {
  return api.get('/portfolios');
};

// GET a single portfolio by ID (for the detail page)
export const getPortfolioById = (id) => {
  return api.get(`/portfolios/${id}`);
};
