import React, { useState } from "react";

function TemplateSelector({ onSelect }) {
  const [selected, setSelected] = useState("");

  const templates = [
    { id: "template1", name: "Modern Template" },
    { id: "template2", name: "Classic Template" }
  ];

  const handleSelect = (id) => {
    setSelected(id);
    if (onSelect) {
      onSelect(id);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Select a Template</h2>
      <div style={{ display: "flex", gap: "20px", marginTop: "10px" }}>
        {templates.map((tpl) => (
          <div
            key={tpl.id}
            onClick={() => handleSelect(tpl.id)}
            style={{
              padding: "20px",
              border: selected === tpl.id ? "3px solid blue" : "1px solid gray",
              borderRadius: "10px",
              cursor: "pointer",
              width: "150px",
              textAlign: "center"
            }}
          >
            {tpl.name}
          </div>
        ))}
      </div>
    </div>
  );
}

export default TemplateSelector;
