import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import API from "../api/api";

// Import all sections
import HeroSection from "../components/sections/HeroSection";
import AboutMe from "../components/sections/AboutMe";
import Skills from "../components/sections/Skills";
import Services from "../components/sections/Services";
import Portfolio from "../components/sections/Portfolio";
import Testimonials from "../components/sections/Testimonials";
import Blog from "../components/sections/Blog";
import Contact from "../components/sections/Contact";
import Footer from "../components/sections/Footer";

function PortfolioPage() {
  const { id } = useParams();
  const [portfolio, setPortfolio] = useState(null);

  useEffect(() => {
    const fetchPortfolio = async () => {
      try {
        const res = await API.get(`/portfolios/${id}`);
        setPortfolio(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchPortfolio();
  }, [id]);

  if (!portfolio) return <div>Loading...</div>;

  return (
    <div>
      <HeroSection hero={portfolio.hero} />
      <AboutMe about={portfolio.about} />
      <Skills skills={portfolio.skills} />
      <Services services={portfolio.services} />
      <Portfolio projects={portfolio.projects} />
      <Testimonials testimonials={portfolio.testimonials} />
      <Blog blog={portfolio.blog} />
      <Contact contact={portfolio.contact} />
      <Footer />
    </div>
  );
}

export default PortfolioPage;
