import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import API from "../api/portfolioApi";

// Import all sections
import HeroSection from "../components/templates/HeroSection";
import AboutMe from "../components/templates/AboutMe";
import Skills from "../components/templates/Skills";
import Services from "../components/templates/Services";
import Portfolio from "../components/templates/Portfolio";
import Testimonials from "../components/templates/Testimonials";
import Blog from "../components/templates/Blog";
import Contact from "../components/templates/Contact";
import Footer from "../components/templates/Footer";

function PortfolioPage() {
  const { id } = useParams();
  const [portfolio, setPortfolio] = useState(null);

  useEffect(() => {
    const fetchPortfolio = async () => {
      try {
        const res = await API.get(`/portfolios/${id}`);
        setPortfolio(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchPortfolio();
  }, [id]);

  if (!portfolio) return <div>Loading...</div>;

  return (
    <div>
      <HeroSection hero={portfolio.hero} />
      <AboutMe about={portfolio.about} />
      <Skills skills={portfolio.skills} />
      <Services services={portfolio.services} />
      <Portfolio projects={portfolio.projects} />
      <Testimonials testimonials={portfolio.testimonials} />
      <Blog blog={portfolio.blog} />
      <Contact contact={portfolio.contact} />
      <Footer />
    </div>
  );
}

export default PortfolioPage;
