import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import API from "../api/portfolioApi";

function ProfilesList() {
  const [portfolios, setPortfolios] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPortfolios = async () => {
      try {
        const res = await API.get("/portfolios");
        setPortfolios(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchPortfolios();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>All Profiles</h2>
      <div style={{ display: "flex", flexWrap: "wrap", gap: "20px", marginTop: "10px" }}>
        {portfolios.map((p) => (
          <div
            key={p._id}
            style={{
              border: "1px solid gray",
              borderRadius: "10px",
              padding: "15px",
              width: "250px",
              cursor: "pointer"
            }}
            onClick={() => navigate(`/portfolio/${p._id}`)}
          >
            <img src={p.hero.profileImage} alt={p.hero.name} style={{ width: "100%", borderRadius: "10px" }} />
            <h3>{p.hero.name}</h3>
            <p>{p.hero.title}</p>
            <p>{p.about.bio?.substring(0, 50)}...</p>
            <div>
              {p.skills?.map((s, i) => (
                <span key={i} style={{ marginRight: "5px", background: "#eee", padding: "2px 5px", borderRadius: "5px" }}>{s}</span>
              ))}
            </div>
            <button style={{ marginTop: "10px" }}>View Portfolio</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProfilesList;
