import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import TemplateSelector from "../components/TemplateSelector";
import MultiSectionForm from "../components/MultiSectionForm";

function Home() {
  const [selectedTemplate, setSelectedTemplate] = useState("");
  const navigate = useNavigate();

  const handleTemplateSelect = (templateId) => {
    setSelectedTemplate(templateId);
  };

  const handleFormSubmit = (portfolioId) => {
    // Form submit ke baad profile page ya list page me redirect
    navigate(`/portfolio/${portfolioId}`);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Dynamic Portfolio Generator</h1>

      <div style={{ marginTop: "20px" }}>
        <TemplateSelector onSelect={handleTemplateSelect} />
      </div>

      {selectedTemplate ? (
        <div style={{ marginTop: "30px" }}>
          <MultiSectionForm templateId={selectedTemplate} onSubmit={handleFormSubmit} />
        </div>
      ) : (
        <p style={{ marginTop: "20px" }}>Please select a template to start building your portfolio.</p>
      )}
    </div>
  );
}

export default Home;
